
def cebra_colores(text ):
   n = 0
   lis= []
   while(len(text) != 2 ):
     blanco = text[0]
     negro = text[1]
     lista =[]
     
     for elem in range(len(text)):
         if text[elem] == blanco and elem % 2 == 0:
             lista.append(blanco)
             continue  
         elif text[elem] == negro:
             lista.append(negro)
         else: break  
     k = len(lista)
     if k > n :
        n = k 
        lis = lista
     text = text[k-1:]

   return n , lis  
    

    

        





